import { Component, OnInit } from "@angular/core";
import { CommonModule } from "@angular/common";
import { FormsModule } from "@angular/forms";
import { ChatApiService, LoadWebsiteResponse, ChatResponse } from "./services/chat-api.service";

interface Message {
  type: "user" | "bot" | "system";
  content: string;
  timestamp: Date;
}

@Component({
  selector: "app-root",
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.css"],
})
export class AppComponent implements OnInit {
  urlInput = "";
  questionInput = "";
  sessionId: string | null = null;
  websiteLoaded = false;
  hostname = "";
  messages: Message[] = [];
  isLoading = false;
  serverConnected = false;

  constructor(private apiService: ChatApiService) {}

  ngOnInit(): void {
    this.checkServerConnection();
  }

  checkServerConnection(): void {
    this.apiService.checkHealth().subscribe({
      next: () => {
        this.serverConnected = true;
        this.addSystemMessage("‚úì Connected to backend server");
      },
      error: () => {
        this.serverConnected = false;
        this.addSystemMessage(
          "‚úó Cannot connect to backend. Make sure the server is running on port 5000"
        );
      },
    });
  }

  loadWebsite(): void {
    if (!this.urlInput.trim()) {
      this.addSystemMessage("Please enter a valid URL");
      return;
    }

    if (!this.serverConnected) {
      this.addSystemMessage("Server is not connected. Cannot load website.");
      return;
    }

    this.isLoading = true;
    this.addSystemMessage(`üì° Loading website: ${this.urlInput}...`);

    this.apiService.loadWebsite(this.urlInput).subscribe({
      next: (response: LoadWebsiteResponse) => {
        this.sessionId = response.sessionId;
        this.websiteLoaded = true;
        this.hostname = response.hostname;
        this.isLoading = false;
        this.messages = [];
        this.addSystemMessage(
          `‚úì ${response.message}\nContent: ${response.contentSize} chars | Chunks: ${response.chunksCount}`
        );
      },
      error: (error) => {
        this.isLoading = false;
        this.addSystemMessage(`‚úó Error: ${error.error?.error || error.message}`);
      },
    });
  }

  sendQuestion(): void {
    if (!this.questionInput.trim()) {
      return;
    }

    if (!this.sessionId) {
      this.addSystemMessage("Load a website first");
      return;
    }

    const question = this.questionInput;
    this.questionInput = "";
    this.isLoading = true;

    this.addMessage("user", question);

    this.apiService.sendQuestion(this.sessionId, question).subscribe({
      next: (response: ChatResponse) => {
        this.isLoading = false;
        this.addMessage("bot", response.answer);
      },
      error: (error) => {
        this.isLoading = false;
        this.addSystemMessage(`‚úó Error: ${error.error?.error || error.message}`);
      },
    });
  }

  addMessage(type: "user" | "bot", content: string): void {
    this.messages.push({
      type,
      content,
      timestamp: new Date(),
    });
  }

  addSystemMessage(content: string): void {
    this.messages.push({
      type: "system",
      content,
      timestamp: new Date(),
    });
  }

  onKeyPress(event: KeyboardEvent): void {
    if (event.key === "Enter" && !event.shiftKey) {
      event.preventDefault();
      this.sendQuestion();
    }
  }
}
