    <h1>üåê Website Q&A Chat</h1>
    <div class="status" [class.connected]="serverConnected">
      <span class="status-dot"></span>
      {{ serverConnected ? "Connected" : "Disconnected" }}
    </div>
  </div>

  <div class="main-content">
    <!-- URL Input Section -->
    <div class="url-section">
      <h2>Load Website</h2>
      <div class="input-group">
        <input
          type="url"
          placeholder="Enter website URL (e.g., https://example.com)"
          [(ngModel)]="urlInput"
          (keyup.enter)="loadWebsite()"
          [disabled]="isLoading || !serverConnected"
          class="url-input"
        />
        <button
          (click)="loadWebsite()"
          [disabled]="isLoading || !serverConnected || !urlInput.trim()"
          class="load-btn"
        >
          {{ isLoading ? "Loading..." : "Load" }}
        </button>
      </div>
      <div *ngIf="websiteLoaded" class="website-info">
        <span class="hostname">üìç {{ hostname }}</span>
      </div>
    </div>

    <!-- Chat Section -->
    <div class="chat-section" *ngIf="websiteLoaded">
      <!-- Messages -->
      <div class="messages">
        <div
          *ngFor="let message of messages"
          [class]="'message message-' + message.type"
        >
          <div class="message-content">{{ message.content }}</div>
          <div class="message-time">{{ message.timestamp | date: "HH:mm" }}</div>
        </div>
        <div *ngIf="isLoading" class="message message-system">
          <div class="message-content">ü§î Thinking...</div>
        </div>
      </div>

      <!-- Question Input -->
      <div class="input-group question-input">
        <textarea
          placeholder="Ask a question about the website... (Shift+Enter for new line, Enter to send)"
          [(ngModel)]="questionInput"
          (keypress)="onKeyPress($event)"
          [disabled]="isLoading"
          rows="2"
          class="question-textarea"
        ></textarea>
        <button
          (click)="sendQuestion()"
          [disabled]="isLoading || !questionInput.trim()"
          class="send-btn"
        >
          {{ isLoading ? "..." : "Send" }}
        </button>
      </div>
    </div>

    <!-- No Website Loaded -->
    <div *ngIf="!websiteLoaded" class="empty-state">
      <div class="empty-icon">üìÑ</div>
      <p>No website loaded yet</p>
      <p class="hint">Load a website to start asking questions</p>
    </div>
  </div>
</div>
