import { Component, OnInit, ViewChild, ElementRef } from "@angular/core";
import { CommonModule } from "@angular/common";
import { FormsModule } from "@angular/forms";
import {
  ChatApiService,
  LoadWebsiteResponse,
  ChatResponse,
  UploadDocumentResponse,
  SummaryResponse,
} from "./services/chat-api.service";

interface Message {
  type: "user" | "bot" | "system";
  content: string;
  timestamp: Date;
}

@Component({
  selector: "app-root",
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.css"],
})
export class AppComponent implements OnInit {
  @ViewChild("fileInput") fileInput!: ElementRef<HTMLInputElement>;

  urlInput = "";
  questionInput = "";
  sessionId: string | null = null;
  contentLoaded = false;
  contentType: "website" | "document" | null = null;
  hostname = "";
  filename = "";
  messages: Message[] = [];
  isLoading = false;
  serverConnected = false;
  summary = "";
  showSummary = false;

  constructor(private apiService: ChatApiService) {}

  ngOnInit(): void {
    this.checkServerConnection();
  }

  checkServerConnection(): void {
    this.apiService.checkHealth().subscribe({
      next: () => {
        this.serverConnected = true;
        this.addSystemMessage("âœ“ Connected to backend server");
      },
      error: () => {
        this.serverConnected = false;
        this.addSystemMessage(
          "âœ— Cannot connect to backend. Make sure the server is running on port 5000"
        );
      },
    });
  }

  loadWebsite(): void {
    if (!this.urlInput.trim()) {
      this.addSystemMessage("Please enter a valid URL");
      return;
    }

    if (!this.serverConnected) {
      this.addSystemMessage("Server is not connected. Cannot load website.");
      return;
    }

    this.isLoading = true;
    this.addSystemMessage(`ðŸ“¡ Loading website: ${this.urlInput}...`);

    this.apiService.loadWebsite(this.urlInput).subscribe({
      next: (response: LoadWebsiteResponse) => {
        this.sessionId = response.sessionId;
        this.contentLoaded = true;
        this.contentType = "website";
        this.hostname = response.hostname;
        this.isLoading = false;
        this.messages = [];
        this.showSummary = false;
        this.addSystemMessage(
          `âœ“ ${response.message}\nContent: ${response.contentSize} chars | Chunks: ${response.chunksCount}`
        );
      },
      error: (error) => {
        this.isLoading = false;
        this.addSystemMessage(`âœ— Error: ${error.error?.error || error.message}`);
      },
    });
  }

  onFileSelected(event: Event): void {
    const input = event.target as HTMLInputElement;
    if (!input.files || input.files.length === 0) return;

    const file = input.files[0];
    this.uploadDocument(file);
  }

  uploadDocument(file: File): void {
    if (!this.serverConnected) {
      this.addSystemMessage("Server is not connected. Cannot upload document.");
      return;
    }

    // Validate file type
    const validTypes = [
      "text/plain",
      "application/pdf",
      "application/msword",
      "application/vnd.openxmlformats-officedocument.wordprocessingml.document",
    ];

    if (!validTypes.includes(file.type) && !file.name.endsWith(".txt") && !file.name.endsWith(".pdf") && !file.name.endsWith(".doc") && !file.name.endsWith(".docx")) {
      this.addSystemMessage("âš  Only .txt, .pdf, and .doc/.docx files are supported");
      return;
    }

    this.isLoading = true;
    this.addSystemMessage(`ðŸ“„ Uploading document: ${file.name}...`);

    this.apiService.uploadDocument(file).subscribe({
      next: (response: UploadDocumentResponse) => {
        this.sessionId = response.sessionId;
        this.contentLoaded = true;
        this.contentType = "document";
        this.filename = response.filename;
        this.isLoading = false;
        this.messages = [];
        this.showSummary = false;
        this.addSystemMessage(
          `âœ“ ${response.message}\nContent: ${response.contentSize} chars | Chunks: ${response.chunksCount}`
        );
      },
      error: (error) => {
        this.isLoading = false;
        this.addSystemMessage(`âœ— Error: ${error.error?.error || error.message}`);
      },
    });

    // Reset file input
    if (this.fileInput) {
      this.fileInput.nativeElement.value = "";
    }
  }

  generateSummary(): void {
    if (!this.sessionId) {
      this.addSystemMessage("Load content first");
      return;
    }

    this.isLoading = true;
    this.addSystemMessage("ðŸ“ Generating summary...");

    this.apiService.getSummary(this.sessionId).subscribe({
      next: (response: SummaryResponse) => {
        this.isLoading = false;
        this.summary = response.summary;
        this.showSummary = true;
        this.addSystemMessage("âœ“ Summary generated!");
      },
      error: (error) => {
        this.isLoading = false;
        this.addSystemMessage(`âœ— Error: ${error.error?.error || error.message}`);
      },
    });
  }

  sendQuestion(): void {
    if (!this.questionInput.trim()) {
      return;
    }

    if (!this.sessionId) {
      this.addSystemMessage("Load website or document first");
      return;
    }

    const question = this.questionInput;
    this.questionInput = "";
    this.isLoading = true;

    this.addMessage("user", question);

    this.apiService.sendQuestion(this.sessionId, question).subscribe({
      next: (response: ChatResponse) => {
        this.isLoading = false;
        this.addMessage("bot", response.answer);
      },
      error: (error) => {
        this.isLoading = false;
        this.addSystemMessage(`âœ— Error: ${error.error?.error || error.message}`);
      },
    });
  }

  addMessage(type: "user" | "bot", content: string): void {
    this.messages.push({
      type,
      content,
      timestamp: new Date(),
    });
  }

  addSystemMessage(content: string): void {
    this.messages.push({
      type: "system",
      content,
      timestamp: new Date(),
    });
  }

  onKeyPress(event: KeyboardEvent): void {
    if (event.key === "Enter" && !event.shiftKey) {
      event.preventDefault();
      this.sendQuestion();
    }
  }
}
<div class="chat-container">
  <div class="header">
    <h1>ðŸŒ Content Q&A & Summarizer</h1>
    <div class="status" [class.connected]="serverConnected">
      <span class="status-dot"></span>
      {{ serverConnected ? "Connected" : "Disconnected" }}
    </div>
  </div>

  <div class="main-content">
    <!-- Left Panel: Content Loader -->
    <div class="left-panel">
      <!-- Input Tabs -->
      <div class="input-tabs">
        <button
          class="tab-btn"
          [class.active]="!contentLoaded"
          (click)="contentLoaded = false"
        >
          ðŸŒ Website
        </button>
        <button
          class="tab-btn"
          [class.active]="contentLoaded && contentType === 'document'"
          (click)="contentLoaded = false"
        >
          ðŸ“„ Document
        </button>
      </div>

      <!-- URL/Document Input Section -->
      <div class="input-section">
        <div class="input-mode-website">
          <h2>Load Website</h2>
          <div class="input-group">
            <input
              type="url"
              placeholder="https://example.com"
              [(ngModel)]="urlInput"
              (keyup.enter)="loadWebsite()"
              [disabled]="isLoading || !serverConnected"
              class="url-input"
            />
            <button
              (click)="loadWebsite()"
              [disabled]="isLoading || !serverConnected || !urlInput.trim()"
              class="load-btn"
            >
              {{ isLoading ? "Loading..." : "Load" }}
            </button>
          </div>
        </div>

        <div class="divider">or</div>

        <div class="input-mode-document">
          <h2>Upload Document</h2>
          <div class="input-group file-upload">
            <label class="file-label">
              <input
                #fileInput
                type="file"
                accept=".txt,.pdf,.doc,.docx"
                (change)="onFileSelected($event)"
                [disabled]="isLoading || !serverConnected"
                class="file-input"
              />
              <span class="file-button">
                {{ isLoading ? "Uploading..." : "Choose File" }}
              </span>
            </label>
            <span class="file-info">TXT, PDF, DOC, DOCX</span>
          </div>
        </div>

        <!-- Loaded Content Info -->
        <div *ngIf="contentLoaded" class="content-info">
          <div class="info-item">
            <span *ngIf="contentType === 'website'" class="info-badge">ðŸ“ {{ hostname }}</span>
            <span *ngIf="contentType === 'document'" class="info-badge">ðŸ“„ {{ filename }}</span>
          </div>
          <button (click)="generateSummary()" [disabled]="isLoading" class="summary-btn">
            ðŸ“ Generate Summary
          </button>
        </div>

        <!-- Summary Display -->
        <div *ngIf="showSummary && summary" class="summary-section">
          <div class="summary-header">
            <h3>ðŸ“ Summary</h3>
            <button (click)="showSummary = false" class="close-btn">âœ•</button>
          </div>
          <div class="summary-content">{{ summary }}</div>
        </div>

        <!-- Empty State -->
        <div *ngIf="!contentLoaded" class="empty-state-left">
          <div class="empty-icon">ðŸ“„</div>
          <p>No content loaded</p>
          <p class="hint">Load a website or upload a document</p>
        </div>
      </div>
    </div>

    <!-- Right Panel: Chat -->
    <div class="right-panel" *ngIf="contentLoaded">
      <div class="chat-section">
        <!-- Messages -->
        <div class="messages">
          <div
            *ngFor="let message of messages"
            [class]="'message message-' + message.type"
          >
            <div class="message-content">{{ message.content }}</div>
            <div class="message-time">{{ message.timestamp | date: "HH:mm" }}</div>
          </div>
          <div *ngIf="isLoading" class="message message-system">
            <div class="message-content">ðŸ¤” Thinking...</div>
          </div>
        </div>

        <!-- Question Input -->
        <div class="input-group question-input">
          <textarea
            placeholder="Ask a question..."
            [(ngModel)]="questionInput"
            (keypress)="onKeyPress($event)"
            [disabled]="isLoading"
            rows="2"
            class="question-textarea"
          ></textarea>
          <button
            (click)="sendQuestion()"
            [disabled]="isLoading || !questionInput.trim()"
            class="send-btn"
          >
            {{ isLoading ? "..." : "Send" }}
          </button>
        </div>
      </div>
    </div>
  </div>
</div>
